windowclass.script:
function onLockChanged()
	if header.subwindow then
		header.subwindow.update();
	end
	if main and main.subwindow then
		main.subwindow.update();
	end

	local bReadOnly = WindowManager.getReadOnlyState(getDatabaseNode());
	notes.setReadOnly(bReadOnly);
end

function onInit()
	update();
end
function update()
	local bReadOnly = WindowManager.getReadOnlyState(getDatabaseNode());
	name.setReadOnly(bReadOnly);
	token.setReadOnly(bReadOnly);
end

function onInit()
	update();
end

function update()
	local bReadOnly = WindowManager.getReadOnlyState(getDatabaseNode());

	talents.setReadOnly(bReadOnly);

	local bSection2 = false;
	if ship_weapons.update(bReadOnly) then bSection2 = true; end;
	divider2.setVisible(bSection2);
end

number_column.script:
function onDragStart(button, x, y, draginfo)
  local label = getName():gsub("^%l", string.upper);
  draginfo.setType("skilldice");
  draginfo.setDescription(label);
  local dice = {"d20"};
  draginfo.setDieList(dice);
  return true;
end

function onDoubleClick(x, y)
  local label = getName():gsub("^%l", string.upper);
  ModifierStack.addSlot(label, getValue());
end

